import React, { Component } from 'react';
import { Input } from 'antd'
const { TextArea } = Input;

class Dashboard extends Component {
    constructor(props) {
        super(props);
    }
    handleCut=(event)=>{
        event.preventDefault();
    }
    render() {
        return (
            <>
            <div className="dashboard-container">
                {/* <TextArea className="text-box" type="textbox" id="confirmEmail" onselectstart="return false" onpaste="return false;" oncopy="return false" oncut="return false" 
                ondrag="return false" ondrop="return false" autocomplete="off">
                    At w3schools.com you will learn how to make a website. We offer free tutorials in all web development technologies.
                </TextArea> */}
                <TextArea id='text'  onPaste={(event)=>this.handlePaste(event)} onCopy={(event)=>this.handleCopy(event)}
                 onCut={(event)=>this.handleCut(event)} onDrag='return false' onDrop='return false' autoComplete='off'>

                </TextArea>
            </div>
            </>
        )
    }
}

export default Dashboard
