import React, { Component } from 'react';
import { Input, Menu, Dropdown, Icon, Button } from 'antd'
const { TextArea } = Input;
const menu = (
    <Menu>
        <Menu.Item key="0">C</Menu.Item>
        <Menu.Divider />
        <Menu.Item key="1">C++</Menu.Item>
        <Menu.Divider />
        <Menu.Item key="3">java</Menu.Item>
        <Menu.Divider />
        <Menu.Item key="3">python</Menu.Item>


    </Menu>
);
class Dashboard extends Component {
    constructor(props) {
        super(props);
    }
    handleCut = (event) => {
        event.preventDefault();
    }
    handleCopy = (event) => {
        event.preventDefault()
    }
    handlePaste = (event) => {
        event.preventDefault();
    }
    handleDrag = (event) => {
        event.preventDefault();
    }
    handleDrop = (event) => {
        event.preventDefault();
    }
    handleSelect = (event) => {
        event.preventDefault();

    }
    render() {
        return (
            <div className="dashboard-container">
                <div className="appbar">
                    <div className="logo-div">
                        <img id="logo" src={require('../assets/BridgeLabz New Logo.svg')} />

                    </div>
                    <span className="title">online-ide</span>
                </div>
                <div className="text-area-div">
                    <TextArea id='text' onSelect={(event) => this.handleSelect(event)} onPaste={(event) => this.handlePaste(event)} onCopy={(event) => this.handleCopy(event)}
                        onCut={(event) => this.handleCut(event)} onDrag={(event) => this.handleDrag(event)} onDrop={(event) => this.handleDrop(event)}>
                    </TextArea>
                </div>
                <div>
                    <p>Language</p>
                    <Dropdown overlay={menu} trigger={['click']}>

                        <Button>
                            Java   <Icon type="down" />
                        </Button>
                    </Dropdown>
                </div>
                <div className="text-area-div">
                    <TextArea id='text'>
                    </TextArea>
                </div>
            </div>

        )
    }
}

export default Dashboard
