import React, { Component } from 'react';
import { Input } from 'antd'
const { TextArea } = Input;

class Dashboard extends Component {
    constructor(props) {
        super(props);
    }
    handleCut = (event) => {
        event.preventDefault();
    }
    render() {
        return (
            <>
                <div className="dashboard-container">
                    <TextArea id='text' onPaste={(event) => this.handlePaste(event)} onCopy={(event) => this.handleCopy(event)}
                        onCut={(event) => this.handleCut(event)} onDrag={(event)=>this.handleDrag(event)} onDrop={(event)=>this.handleDrop(event)} autoComplete='off'>

                    </TextArea>
                </div>
            </>
        )
    }
}

export default Dashboard
